export default {
  name: 'button-random-gradient',
  data: () => ({
    randomGradient: ''
  }),
  methods: {
    sendColors() {
      this.$emit('new-gradient', {
        r: this.generateColor(0, 255),
        g: this.generateColor(0, 255),
        b: this.generateColor(0, 255)
      });
    },
    generateColor(min, max) {
      return min + Math.floor(Math.random() * (max - min + 1));
    }
  }
};
